// This method will allow the app to have a compiler and run code from a variety of different languages
const fetch = require("node-fetch");
const got = require("got");

const accessToken = "8ca5858d335f9887bfda937a1517cbae";
const endpoint = "c8f4cd08.compilers.sphere-engine.com";

const languages = {
  java: 10,
  c: 11,
  csharp: 27,
  cpp: 44,
  js: 56,
  python: 116,
};

const runCode = async (code, language) => {
  const output = await postSubmission(code, language);
  return output;
};

// The method will take in the string containing the code to compile. It will also take in a string that will
// determine the language to compile
const postSubmission = async (code, language) => {
  const submission = await fetch(
    "https://" + endpoint + "/api/v4/submissions?access_token=" + accessToken,
    {
      method: "POST",
      body: JSON.stringify({
        compilerId: languages[language],
        source: code,
      }),
      headers: { "Content-Type": "application/json" },
    }
  );
  const submissionJSON = await submission.json();
  const submissionID = submissionJSON.id;
  let output = "";
  setTimeout(async () => {
    output = await getSubmission(submissionID);
  }, 2000);
  return output;
};

const getSubmission = async (submissionID) => {
  const result = await fetch(
    "https://" +
      endpoint +
      "/api/v4/submissions/" +
      submissionID +
      "?access_token=" +
      accessToken,
    {
      method: "GET",
    }
  );

  const finalResult = await result.json();
  let output = "";
  if (finalResult.executing === true) {
    setTimeout(async () => {
      output = await getSubmission(submissionID);
    }, 2000);
    return output;
  } else {
    if (finalResult.result.streams.output) {
      const uri = finalResult.result.streams.output.uri;
      const response = await got(uri);
      console.log(response.body);
      return response.body;
    } else {
      const uri = finalResult.result.streams.cmpinfo.uri;
      const response = await got(uri);
      return response.body;
    }
  }
};
postSubmission(
  `
public class Main {
  public static void main(String[] args) {
    System.out.print("Hi");
  }
}
`,
  "java"
);
